import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { ThemeService } from './modules/shared/services/theme.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  title = 'project-management-app';

  public darkTheme: Observable<boolean>;

  constructor(private themeService: ThemeService) {}

  ngOnInit(): void {
    this.darkTheme = this.themeService._darkTheme$$;
  }
}
<div class="h-full flex flex-col">
  <app-header class="z-10 mat-app-background" [ngClass]="{ 'dark-theme': darkTheme | async }"></app-header>
  <main class="flex-grow mat-app-background" [ngClass]="{ 'dark-theme': darkTheme | async }">
    <app-sidebar class="mat-app-background" [ngClass]="{ 'dark-theme': darkTheme | async }"></app-sidebar>
    <router-outlet></router-outlet>
  </main>
  <app-footer class="z-10 mat-app-background" [ngClass]="{ 'dark-theme': darkTheme | async }"></app-footer>
</div>
