  <mat-sidenav #sidenav opened="true" mode="side"
               class="m-4 border-r-0 bg-gray-400 bg-opacity-25 p-4 rounded-md">

    <mat-divider class="mt-4 mb-4 bg-amber-400"></mat-divider>

    <mat-nav-list>
      <a mat-list-item routerLink="/" class="hover:cursor-pointer">
        <mat-icon>home</mat-icon>
        <span>Home</span>
      </a>
      <a mat-list-item routerLink="/user/signup" class="hover:cursor-pointer">
        <mat-icon>supervised_user_circle</mat-icon>
        <span>Sign Up</span>
      </a>
      <a mat-list-item routerLink="/user/signin" class="hover:cursor-pointer">
        <mat-icon>account_circle</mat-icon>
        <span>Sign In</span>
      </a>
    </mat-nav-list>

    <mat-divider></mat-divider>

  </mat-sidenav>
  <mat-sidenav-content>
    <div class="h-screen rounded-sm p-4">
      <router-outlet></router-outlet>
    </div>
  </mat-sidenav-content>
</mat-sidenav-container>
